  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.renderForm = this.renderForm.bind(this);
    this.renderPoints = this.renderPoints.bind(this);
    this.submitForm = this.submitForm.bind(this);
    this.getData = this.getData.bind(this);
    this.logout = this.logout.bind(this);
  }
  componentDidMount() {
    let requestURL =
      "https://ca.elan.org.in/doesuserexist/" +
      JSON.parse(localStorage.getItem("email")).email;
    axios.get(requestURL).then(res => {
      this.setState({ firstTime: !(res.data.message == "true") });
    });
  }
  handleInputChange(event) {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }
  getData() {
    const headers = {
      headers: {
        "Content-Type": "application/json",
        authorization: "Bearer " + localStorage.getItem("access_token")
      }
    };
    let url =
      "https://ca.elan.org.in/api/private?token=" +
      localStorage.getItem("id_token");
    axios
      .get(url, headers)
      .then(res => {
        this.setState({
          fname: res.data.fname,
          lname: res.data.lname,
          no_of_people: res.data.no_of_people,
          points: res.data.points,
          rcode: res.data.rcode
        });
      })
      .catch(error => {
        localStorage.clear();
        window.location.reload();
      });
  }
  submitForm(event) {
    event.preventDefault();
    const headers = {
      headers: {
        "Content-Type": "application/json",
        authorization: "Bearer " + localStorage.getItem("access_token")
      }
    };
    let url =
      "https://ca.elan.org.in/api/private?fname=" +
      this.state.fname +
      "&lname=" +
      this.state.lname +
      "&institute=" +
      this.state.institute +
      "&age=" +
      this.state.age +
      "&token=" +
      localStorage.getItem("id_token") +
      "&fb=" +
      this.state.fbHandle +
      "&insta=" +
      this.state.instaHandle +
      "&ideas=" +
      this.state.ideas +
      "&phone=" +
      this.state.contactNumber +
      "&referredBy=" +
      this.state.referredBy;
    axios
      .get(url, headers)
      .then(res => {
        window.location.reload();
      })
      .catch(error => {
        localStorage.clear();
        window.location.reload();
      });
  }

  renderForm() {
    return (
      <div id="fh5co-couple" style={{ paddingTop: "20px" }}>
        <div className="container">
          <div className="row">
            <div className="col-md-8 col-md-offset-2 text-center fh5co-heading animate-box">
              <h2>Registration Portal</h2>
              <h3>For Campus Ambassadors</h3>
              <Form style={{ marginTop: "50px" }} onSubmit={this.submitForm}>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="fname"
                        value={this.state.fname}
                        placeholder="First Name"
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="lname"
                        value={this.state.lname}
                        placeholder="Last Name"
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="institute"
                        placeholder="Institute"
                        value={this.state.institute}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="age"
                        value={this.state.age}
                        onChange={this.handleInputChange}
                        placeholder="Age"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="fbHandle"
                        placeholder="Link to your Facebook Profile"
                        value={this.state.fbHandle}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="instaHandle"
                        value={this.state.instaHandle}
                        onChange={this.handleInputChange}
                        placeholder="Instagram Handle"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={12}>
                    <FormGroup>
                      <Input
                        type="textarea"
                        name="ideas"
                        placeholder="What are your ideas for publicising the fest?"
                        value={this.state.ideas}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={12}>
                    <FormGroup>
                      <Input
                        type="textarea"
                        name="contactNumber"
                        placeholder="Contact Number"
                        value={this.state.contactNumber}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={12}>
                    <FormGroup>
                      <Input
                        type="textarea"
                        name="referredBy"
                        placeholder="Were you referred here by another CA? If yes, enter their referral code here. "
                        value={this.state.referredBy}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Button color="primary" style={{ marginTop: "30px" }}>
                    Submit
                  </Button>
                </Row>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
  logout() {
    localStorage.clear();
    window.location.reload();
  }
  renderPoints() {
    if (this.state.no_of_people == null) {
      this.getData();
      return <div class="fh5co-loader"></div>;
    } else {
      return (
        <div id="fh5co-couple" style={{ paddingTop: "20px" }}>
          <div className="container">
            <div className="row">
              <div className="col-md-8 col-md-offset-2 text-center fh5co-heading animate-box">
                <h2>Elan & nVision 2020</h2>
                <h3 style={{ marginTop: "40px" }}>
                  Welcome, {this.state.fname} {this.state.lname}
                </h3>
                {/* <h4>Your referral code is {this.state.rcode}.</h4>
                <h4>
                  You have been awarded {this.state.points} points since{" "}
                  {this.state.no_of_people} people have used your referral code.
                </h4> */}
                <h4>
                  Your referral code is {this.state.rcode}. When people use this
                  code to sign up, you will be awarded points.
                </h4>
                <h4>
                  So far, {this.state.no_of_people} people have used your
                  referral code and you've been awarded {this.state.points}{" "}
                  points.
                </h4>
                <Button color="primary" onClick={this.logout}>
                  LOGOUT
                </Button>
              </div>
            </div>
          </div>
        </div>
      );
    }
  }
  render() {
    if (this.state.firstTime === true) {
      return <this.renderForm />;
    } else if (this.state.firstTime === false) {
      return <this.renderPoints />;
    } else {
      return <div class="fh5co-loader"></div>;
    }
  }
}

export default App;
