import React from "react";
import { Form, FormGroup, Col, Input, Row, Button } from "reactstrap";
import $ from "jquery";
import auth0 from "auth0-js";
import "./index.css";
import axios from "axios";

let globalRootURL = "http://" + window.location.host;

const AUTH0_CLIENT_ID = "7ypOoXT5lMAP6B3LRvGJoD3eK0tBTrai";
const AUTH0_DOMAIN = "elan-nvision.auth0.com";
const AUTH0_API_AUDIENCE = "https://elan-nvision.auth0.com/api/v2/";
const AUTH0_CALLBACK_URL = globalRootURL;

class App extends React.Component {
  parseHash() {
    this.auth0 = new auth0.WebAuth({
      domain: AUTH0_DOMAIN,
      clientID: AUTH0_CLIENT_ID
    });
    this.auth0.parseHash((err, authResult) => {
      if (err) {
        return console.log(err);
      }
      if (
        authResult !== null &&
        authResult.accessToken !== null &&
        authResult.idToken !== null
      ) {
        localStorage.setItem("access_token", authResult.accessToken);
        localStorage.setItem("id_token", authResult.idToken);
        localStorage.setItem(
          "email",
          JSON.stringify(authResult.idTokenPayload)
        );
        window.location = window.location.href.substr(
          0,
          window.location.href.indexOf("")
        );
      }
    });
  }
  setup() {
    $.ajaxSetup({
      beforeSend: function(xhr) {
        if (localStorage.getItem("access_token")) {
          xhr.setRequestHeader(
            "Authorization",
            "Bearer " + localStorage.getItem("access_token")
          );
        }
      }
    });
  }
  setState() {
    let idToken = localStorage.getItem("id_token");
    if (idToken) {
      this.loggedIn = true;
    } else {
      this.loggedIn = false;
    }
  }
  componentWillMount() {
    this.setup();
    this.parseHash();
    this.setState();
  }
  renderBody() {
    if (this.loggedIn) return <LoggedIn />;
    else return <Home />;
  }

  render() {
    return this.loggedIn === undefined ? (
      <div className="loader">Loading</div>
    ) : (
      this.renderBody()
    );
  }
}

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.authenticate = this.authenticate.bind(this);
  }
  componentDidMount() {
    this.authenticate();
  }
  authenticate() {
    this.WebAuth = new auth0.WebAuth({
      domain: AUTH0_DOMAIN,
      clientID: AUTH0_CLIENT_ID,
      scope: "openid email",
      audience: AUTH0_API_AUDIENCE,
      responseType: "token id_token",
      redirectUri: AUTH0_CALLBACK_URL
    });
    this.WebAuth.authorize();
  }

  render() {
    return <div></div>;
  }
}

class LoggedIn extends React.Component {
  state = { firstTime: null, fname: "", lname: "", institute: "", age: null };
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.renderForm = this.renderForm.bind(this);
    this.renderPoints = this.renderPoints.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }
  componentDidMount() {
    let requestURL =
      "http://localhost:8080/doesuserexist/" +
      JSON.parse(localStorage.getItem("email")).email;
    axios.get(requestURL).then(res => {
      this.setState({ firstTime: !(res.data.message == "true") });
    });
  }
  handleInputChange(event) {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }
  submitForm(event) {
    event.preventDefault();
    const headers = {
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("access_token"),
        Id_token: localStorage.getItem("id_token")
      }
    };
    let url =
      "http://localhost:8080/api/private?fname=" +
      this.state.fname +
      "&lname=" +
      this.state.lname +
      "&institute=" +
      this.state.institute +
      "&age=" +
      this.state.age;
    axios.get(url, headers).then(res => {
      console.log(res);
    });
  }

  renderForm() {
    return (
      <div id="fh5co-couple" style={{ paddingTop: "20px" }}>
        <div className="container">
          <div className="row">
            <div className="col-md-8 col-md-offset-2 text-center fh5co-heading animate-box">
              <h2>Registration Portal</h2>
              <h3>For Campus Ambassadors</h3>
              <Form style={{ marginTop: "50px" }} onSubmit={this.submitForm}>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="fname"
                        value={this.state.fname}
                        placeholder="First Name"
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="lname"
                        value={this.state.lname}
                        placeholder="Last Name"
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row form>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="institute"
                        placeholder="Institute"
                        value={this.state.institute}
                        onChange={this.handleInputChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col md={6}>
                    <FormGroup>
                      <Input
                        type="text"
                        name="age"
                        value={this.state.age}
                        onChange={this.handleInputChange}
                        placeholder="Age"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Button color="primary" style={{ marginTop: "30px" }}>
                    Submit
                  </Button>
                </Row>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
  renderPoints() {
    return <div>Points</div>;
  }
  render() {
    console.log(this.state.firstTime);
    if (this.state.firstTime === true) {
      return <this.renderForm />;
    } else if (this.state.firstTime === false) {
      return <p>Points</p>;
    } else {
      return <p>Loading</p>;
    }
  }
}

export default App;
